import React from "react";
import "./userCard.css"; // UserCard-specific CSS

const UserCard = ({ ticket }) => {
  const { id, title, user, status } = ticket;

  return (
    <div className="user-card">
      <div className="user-card-header">
        <span className="user-card-id">Ticket ID: {id}</span>
        <span className="user-card-user">Assigned to: {user}</span>
      </div>
      <div className="user-card-body">
        <h4 className="user-card-title">{title}</h4>
        <div className="user-card-status">{status}</div>
      </div>
    </div>
  );
};

export default UserCard;
