// src/components/DisplayOptions.js
import React from 'react';

const DisplayOptions = ({ setGrouping, setSorting }) => {
    return (
      <div className="display-options">
        <div className="display-option">
          <label htmlFor="grouping">Grouping:</label>
          <select id="grouping" onChange={(e) => setGrouping(e.target.value)}>
            <option value="status">Status</option>
            <option value="priority">Priority</option>
          </select>
        </div>
        <div className="display-option">
          <label htmlFor="sorting">Ordering:</label>
          <select id="sorting" onChange={(e) => setSorting(e.target.value)}>
            <option value="priority">Priority</option>
            <option value="title">Title</option>
            <option value="createdAt">Created At</option>
            <option value="updatedAt">Updated At</option>
          </select>
        </div>
      </div>
    );
  };


export default DisplayOptions;
