import React from 'react';
import './Card.css';

// Import SVGs
import LockIcon from '../assets/Done.svg';
import WarningIcon from '../assets/in-progess.svg';
import ClipboardIcon from '../assets/To-do.svg';

const Card = () => {
  return (
    <div className="card">
      <div className="card-header">
        <span className="card-id">CAM-11</span>
        <div className="avatar">
          <img src="https://via.placeholder.com/30" alt="user avatar" />
        </div>
      </div>

      {/* Top SVG Icon */}
      <div className="top-icon">
        <img src={LockIcon} alt="lock icon" className="icon-svg" />
      </div>

      {/* Title with SVG Icon */}
      <div className="card-title-container">
        <div className="icon-title">
          <img src={WarningIcon} alt="warning icon" className="icon-svg title-icon" />
          <h3 className="card-title">Conduct Security Vulnerability Assessment</h3>
        </div>
      </div>

      {/* Footer with SVG Icon */}
      <div className="card-footer">
        <div className="footer-icon">
          <img src={ClipboardIcon} alt="clipboard icon" className="icon-svg" />
        </div>
        <div className="feature-tag">Feature Request</div>
      </div>
    </div>
  );
};

export default Card;
