import React, { useState, useEffect } from 'react';
import { fetchTickets } from '../Api';
import Card from './Card';
import DisplayOptions from './DisplayOptions';
import './Board.css'; // For custom CSS

const Board = () => {
  const [tickets, setTickets] = useState([]);
  const [grouping, setGrouping] = useState('status');
  const [sorting, setSorting] = useState('priority');

  useEffect(() => {
    const getTickets = async () => {
      const data = await fetchTickets();
      setTickets(data.tickets);
    };
    getTickets();
  }, []);


  useEffect(() => {
    const savedGrouping = localStorage.getItem('grouping');
    const savedSorting = localStorage.getItem('sorting');

    if (savedGrouping) setGrouping(savedGrouping);
    if (savedSorting) setSorting(savedSorting);
  }, []);

  // Save grouping and sorting preferences to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('grouping', grouping);
    localStorage.setItem('sorting', sorting);
  }, [grouping, sorting]);

  const groupTickets = () => {
    let groupedTickets = [];

    // Group by status, user, or priority
    if (grouping === 'status') {
      groupedTickets = groupBy(tickets, 'status');
    } else if (grouping === 'user') {
      groupedTickets = groupBy(tickets, 'user');
    } else {
      groupedTickets = groupBy(tickets, 'priority');
    }

    // Sorting logic
    if (sorting === 'priority') {
      groupedTickets.forEach(group => group.tickets.sort((a, b) => b.priority - a.priority));
    } else {
      groupedTickets.forEach(group => group.tickets.sort((a, b) => a.title.localeCompare(b.title)));
    }

    return groupedTickets;
  };

  const groupBy = (arr, key) => {
    const grouped = arr.reduce((acc, curr) => {
      (acc[curr[key]] = acc[curr[key]] || []).push(curr);
      return acc;
    }, {});

    return Object.keys(grouped).map(key => ({
      title: key,
      tickets: grouped[key],
    }));
  };

  return (
    <div className="board-container">
      {/* Display Options Component */}

      <div className="kanban-board">
        {groupTickets().map((group) => (
          <div key={group.title} className="group-column">
            <h3>{group.title}</h3>
            {group.tickets.map((ticket) => (
              <Card key={ticket.id} ticket={ticket} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Board;
