import React from "react";
import "./Card.css"; // Import card-specific styles

// Sample icons for ticket status
import UncheckedIcon from "../assets/To-do.svg";
import InProgressIcon from "../assets/in-progress.svg";
import CompletedIcon from "../assets/Done.svg";
import BacklogIcon from "../assets/Backlog.svg";

// Status-to-icon mapping function
const getStatusIcon = (status) => {
  switch (status) {
    case "Todo":
      return UncheckedIcon;
    case "In progress":
      return InProgressIcon;
    case "Completed":
      return CompletedIcon;
    case "Backlog":
      return BacklogIcon;
    default:
      return UncheckedIcon;
  }
};

const Card = ({ ticket }) => {
  const { id, title, status, tag } = ticket;

  return (
    <div className="card">
      <div className="card-header">
        <span className="card-id">#{id}</span>
        <img src={getStatusIcon(status)} alt={status} className="status-icon" />
      </div>
      <div className="card-body">
        <h4 className="card-title">{title}</h4>
      </div>
      {tag && (
        <div className="card-footer">
          <div className="tag">{tag}</div>
        </div>
      )}
    </div>
  );
};

export default Card;
