import React, { useState, useEffect } from "react";
import { fetchTickets } from "../Api"; // Mock API call to fetch tickets
import Column from "./Column"; // Import Column component
import "./Board.css"; // Import board-specific styles

import TopBar from './Topbar'; // Import the TopBar component

// Priority labels mapping
const priorityLabels = {
  "1": "Urgent",
  "2": "High",
  "3": "Medium",
  "4": "Low",
  "5": "No priority",
};

const Board = () => {
  const [tickets, setTickets] = useState([]);
  const [users, setUsers] = useState([]);
  const [grouping, setGrouping] = useState("priority");
  const [sorting, setSorting] = useState("priority");

  // Fetch tickets and users from the API
  useEffect(() => {
    const getTicketsAndUsers = async () => {
      const data = await fetchTickets();
      setTickets(data.tickets);
      setUsers(data.users); // Store the users data as well
    };
    getTicketsAndUsers();
  }, []);

  // Function to get the username from userId
  const getUserName = (userId) => {
    const user = users.find((user) => user.id === userId);
    return user ? user.name : "Unknown User";
  };

  // Group tickets by a specific key (e.g., priority or userId)
  const groupBy = (arr, key) => {
    const grouped = arr.reduce((acc, curr) => {
      const groupKey = key === "username" ? getUserName(curr.userId) : curr[key];
      (acc[groupKey] = acc[groupKey] || []).push(curr);
      return acc;
    }, {});
    return Object.keys(grouped).map((key) => ({
      title: key,
      tickets: grouped[key],
    }));
  };

  const groupTickets = () => {
    let groupedTickets = groupBy(tickets, grouping === "userId" ? "username" : grouping);

    // Apply the priority labels if grouping by priority
    if (grouping === "priority") {
      groupedTickets = groupedTickets.map((group) => ({
        ...group,
        title: priorityLabels[group.title] || "No priority",
      }));
    }

    // Sort tickets within each group
    groupedTickets.forEach((group) => {
      if (sorting === "priority") {
        group.tickets.sort((a, b) => b.priority - a.priority);
      } else {
        group.tickets.sort((a, b) => a.title.localeCompare(b.title));
      }
    });

    return groupedTickets;
  };

  return (
    <div className="board-container">
      <TopBar setGrouping={setGrouping} setSorting={setSorting} />
      <div className="kanban-board">
        {groupTickets().map((group) => (
          <Column key={group.title} title={group.title} tickets={group.tickets} count={group.tickets.length} />
        ))}
      </div>
    </div>
  );
};

export default Board;
