// src/components/Card.js
import React from 'react';
import './Card.css'; // For custom CSS

const Card = ({ ticket }) => {
  return (
    <div className="ticket-card">
      <h4>{ticket.title}</h4>
      <p>{ticket.description}</p>
      <p>Priority: {ticket.priority}</p>
      <p>Status: {ticket.status}</p>
    </div>
  );
};

export default Card;
