import React, { useState, useEffect } from 'react';
import { fetchTickets } from '../Api';
import Card from './Card';
import DisplayOptions from './DisplayOptions';
import TopBar from './Topbar'; // Import the TopBar component
import './Board.css'; // For custom CSS

const Board = () => {
  const [tickets, setTickets] = useState([]);
  const [grouping, setGrouping] = useState('status');
  const [sorting, setSorting] = useState('tag'); // Change sorting to tag

  // Mapping of ticket statuses to display names
  const titleMapping = {
    'No priority': '0 - No priority',
    'High': '1 - High',
    'Medium': '2 - Medium',
    'Low': '3 - Low',
    // Add any other mappings as needed
  };

  useEffect(() => {
    const getTickets = async () => {
      const data = await fetchTickets();
      setTickets(data.tickets);
    };
    getTickets();
  }, []);

  useEffect(() => {
    const savedGrouping = localStorage.getItem('grouping');
    const savedSorting = localStorage.getItem('sorting');

    if (savedGrouping) setGrouping(savedGrouping);
    if (savedSorting) setSorting(savedSorting);
  }, []);

  useEffect(() => {
    localStorage.setItem('grouping', grouping);
    localStorage.setItem('sorting', sorting);
  }, [grouping, sorting]);

  const groupTickets = () => {
    let groupedTickets = [];

    if (grouping === 'status') {
      groupedTickets = groupBy(tickets, 'status');
    } else if (grouping === 'user') {
      groupedTickets = groupBy(tickets, 'user');
    } else {
      groupedTickets = groupBy(tickets, 'tag'); // Change grouping to tag if needed
    }

    // Modify sorting to handle cases where tag may not be a string
    if (sorting === 'tag') { // Sort by tag instead of priority
      groupedTickets.forEach(group => {
        group.tickets.sort((a, b) => {
          const tagA = typeof a.tag === 'string' ? a.tag : '';
          const tagB = typeof b.tag === 'string' ? b.tag : '';
          return tagA.localeCompare(tagB);
        });
      });
    } else {
      groupedTickets.forEach(group => group.tickets.sort((a, b) => a.title.localeCompare(b.title)));
    }

    return groupedTickets;
  };

  const groupBy = (arr, key) => {
    const grouped = arr.reduce((acc, curr) => {
      (acc[curr[key]] = acc[curr[key]] || []).push(curr);
      return acc;
    }, {});

    return Object.keys(grouped).map(key => ({
      title: key,
      tickets: grouped[key],
    }));
  };

  return (
    <div className="board-container" style={{ paddingTop: '70px' }}> {/* Adjust padding for top bar */}
      <TopBar setGrouping={setGrouping} setSorting={setSorting} />
      <div className="kanban-board">
        {groupTickets().map((group) => (
          <div key={group.title} className="group-column">
            <h3>{titleMapping[group.title] || group.title}</h3> {/* Use mapping for display */}
            {group.tickets.map((ticket) => (
              <Card key={ticket.id} ticket={ticket} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Board;
