import React, { useState, useEffect } from "react";
import { fetchTickets } from "../Api"; // Mock API call to fetch tickets
import Column from "./Column"; // Import Column component
import "./Board.css"; // Import board-specific styles
import TopBar from "./Topbar"; // Import the TopBar component

// Priority labels mapping
const priorityLabels = {
  "1": "Urgent",
  "2": "High",
  "3": "Medium",
  "4": "Low",
  "5": "No priority",
};

const Board = () => {
  const [tickets, setTickets] = useState([]);
  const [grouping, setGrouping] = useState("priority"); // Default grouping by priority
  const [sorting, setSorting] = useState("priority"); // Default sorting by priority

  // Fetch tickets from an API (mock or real)
  useEffect(() => {
    const getTickets = async () => {
      const data = await fetchTickets(); // Replace with actual API call
      setTickets(data.tickets);
    };
    getTickets();
  }, []);

  // Group tickets by a specific key (priority, user, etc.)
  const groupBy = (arr, key) => {
    const grouped = arr.reduce((acc, curr) => {
      (acc[curr[key]] = acc[curr[key]] || []).push(curr);
      return acc;
    }, {});
    return Object.keys(grouped).map((key) => ({
      title: key,
      tickets: grouped[key],
    }));
  };

  const groupTickets = () => {
    let groupedTickets = groupBy(tickets, grouping); // Group by priority or user

    // Apply priority labels if grouping by priority
    if (grouping === "priority") {
      groupedTickets = groupedTickets.map((group) => ({
        ...group,
        title: priorityLabels[group.title] || "No priority",
      }));
    }

    // Sort tickets within each group by the selected sorting method (priority or title)
    groupedTickets.forEach((group) => {
      if (sorting === "priority") {
        group.tickets.sort((a, b) => b.priority - a.priority); // Sort by priority (highest to lowest)
      } else if (sorting === "title") {
        group.tickets.sort((a, b) => a.title.localeCompare(b.title)); // Sort alphabetically by title
      }
    });

    return groupedTickets;
  };

  return (
    <div className="board-container">
      {/* TopBar allows users to choose grouping and sorting */}
      <TopBar setGrouping={setGrouping} setSorting={setSorting} />
      <div className="kanban-board">
        {groupTickets().map((group) => (
          <Column
            key={group.title}
            title={group.title}
            tickets={group.tickets}
            count={group.tickets.length}
          />
        ))}
      </div>
    </div>
  );
};

export default Board;
