const Board = () => {
  const [tickets, setTickets] = useState([]);
  const [grouping, setGrouping] = useState("priority");
  const [sorting, setSorting] = useState("priority");

  // Add the usernames directly in the tickets data, no need for a users object
  useEffect(() => {
    const getTickets = async () => {
      const data = await fetchTickets(); // Replace with actual API call
      // Map userId to username directly inside tickets data
      const mappedTickets = data.tickets.map(ticket => ({
        ...ticket,
        username: getUserName(ticket.userId) // Assuming getUserName is a function that maps userId to name
      }));
      setTickets(mappedTickets);
    };
    getTickets();
  }, []);

  // Function to group tickets by a specific key (now directly by 'username')
  const groupBy = (arr, key) => {
    const grouped = arr.reduce((acc, curr) => {
      (acc[curr[key]] = acc[curr[key]] || []).push(curr);
      return acc;
    }, {});
    return Object.keys(grouped).map((groupName) => ({
      title: groupName, // This will now be the username if grouped by 'username'
      tickets: grouped[groupName],
    }));
  };

  const groupTickets = () => {
    let groupedTickets = groupBy(tickets, grouping === "username" ? "username" : grouping);

    // Apply priority labels if grouping by priority
    if (grouping === "priority") {
      groupedTickets = groupedTickets.map((group) => ({
        ...group,
        title: priorityLabels[group.title] || "No priority",
      }));
    }

    // Sort tickets within each group
    groupedTickets.forEach((group) => {
      if (sorting === "priority") {
        group.tickets.sort((a, b) => b.priority - a.priority);
      } else {
        group.tickets.sort((a, b) => a.title.localeCompare(b.title));
      }
    });

    return groupedTickets;
  };

  return (
    <div className="board-container">
      <TopBar setGrouping={setGrouping} setSorting={setSorting} />
      <div className="kanban-board">
        {groupTickets().map((group) => (
          <Column key={group.title} title={group.title} tickets={group.tickets} count={group.tickets.length} />
        ))}
      </div>
    </div>
  );
};

export default Board;
